        public static string DND(int 次数, string 表达式)
        {
            if (次数 > 10)
            {
                次数 = 10;
            }
            if (次数 < 1)
            {
                次数 = 1;
            }
            if (表达式.Length < 5)
            {
                表达式 = "R4D6K3";
            }
            string 返回值 = 获取昵称() + "DND英雄作成：\n";
            for (int i = 0; i < 次数; i++)
            {
                string 力量 = 补全(骰点(表达式));
                string 体质 = 补全(骰点(表达式));
                string 敏捷 = 补全(骰点(表达式));
                string 智力 = 补全(骰点(表达式));
                string 感知 = 补全(骰点(表达式));
                string 魅力 = 补全(骰点(表达式));
                返回值 += $"力量:{力量} 体质:{体质} 敏捷:{敏捷} 智力:{智力} 感知:{感知} 魅力:{魅力} ";
                返回值 += $"共计:{new DataTable().Compute(力量 + "+" + 体质 + "+" + 敏捷 + "+" + 智力 + "+" + 感知 + "+" + 魅力, "")}\n";
            }
            return 返回值;
        }

        public static string 症状发作(bool 临时)
        {
            string 昵称 = 获取昵称();
            int rd = new Random(Guid.NewGuid().GetHashCode()).Next(0, Convert.ToInt32(10));
            if (临时)
            {
                return $"{昵称}疯狂发作-临时症状:\n{临时症状[rd]}";
            }
            return $"{昵称}疯狂发作-总结症状:\n{总结症状[rd]}";
        }

        public static string 今日人品()
        {
            string 昵称 = 获取昵称();
            string 今天 = (Convert.ToInt32(数据.实体["现在"]["时间"]) / 1000000).ToString();
            string 返回值 = "";
            if (数据.实体.ContainsKey(数据.私聊目标.FromQQ.ToString()))
            {
                if (数据.实体[数据.私聊目标.FromQQ.ToString()].ContainsKey("今日人品"))
                {
                    if (数据.实体[数据.私聊目标.FromQQ.ToString()]["今日人品"].StartsWith(今天))
                    {
                        返回值 = 数据.实体[数据.私聊目标.FromQQ.ToString()]["今日人品"];
                        返回值 = 返回值.Substring(返回值.IndexOf("：") + 1);
                        return 昵称 + "今日人品是：" + 返回值;
                    }
                }
            }
            运算.骰子("R2D100K1");
            数据.写入实体(数据.私聊目标.FromQQ.ToString(), "今日人品", $"{今天}：{数据.读取组件("上次的骰点")}");
            返回值 = 数据.实体[数据.私聊目标.FromQQ.ToString()]["今日人品"];
            返回值 = 返回值.Substring(返回值.IndexOf("：") + 1);
            return 昵称 + "今日人品是：" + 返回值;
        }

        static string 补全(int 数字)
        {
            string 返回值 = 数字.ToString();
            if (返回值.Length == 1)
            {
                返回值 = 返回值.Insert(0, "  ");
            }
            return 返回值;
        }

        static int 骰点(string 表达式)
        {
            运算.骰子(表达式);
            return Convert.ToInt32(读取组件("上次的骰点"));
        }
    }
